para.literalLayout:
/* BASIC SAS PROGRAM FOR READING VCR-LTER WATER LEVEL RECORDER DATA 
   THIS VERSION READS RAW FILES FROM THE DATA LOGGER AND DOES ERROR
   CHECKS
*/ 
options pagesize=62 linesize=80; 
%let year=05; 
* if a NEW year, also see commented SET statement down below;
LIBNAME LIB '../lib'; 


/* This data step reads in the data (with duplicate observations) and does 

adjustments to mean sea level etc. 
*/ 


Data Newhour; 


/* Change the name of the file in quotes to match the file name you want 
as 
    input to this program */
  INFILE 'NEWDATA.DAT' MISSOVER dlm=',' dsd missover; 
  Length SerialNo $ 8;
  Length Line  $ 1 ;
  Length tmpdate $ 9 ;
  Length Location $ 20 ;
  Length qualcode $ 1;
  INPUT LINE $ 1 @;
  IF (LINE EQ 'S') then do;
        input @12 Serial $ ;
        SerialNo='S'||Serial; /* add S to prevent interpretation as number 
*/
  END;
  ELSE IF (LINE EQ 'D') then DO;
        input @17 dumpmon $3. +1 dumpday $2. +1 dumptim TIME8. +1 dumpyear 
$4.;
        tmpdate=dumpday||dumpmon||dumpyear;
        dumpdate=DHMS(input(tmpdate,DATE9.),
                 hour(dumptim),minute(dumptim),second(dumptim));
  END;
  ELSE input obsnum levelin +2 timein TIME8. +4 datein DATE10. ;
  Retain SerialNo dumpdate dumpmon dumpday dumpyear dumptim;
  datelev=DHMS(datein,hour(timein),minute(timein),second(timein));
  format datelev datetime. ;
  format dumpdate datetime. ;
  drop tmpdate line dumpmon dumpday dumptim dumpyear timein datein serial;

* keep only valid dates and dates in the current year;
  if (datelev eq .) then delete;
  if (year(datein) EQ (2000 + &year)); 


/* Assign serial numbers to locations */
  if (serialno eq 'S021408D') then do;
        location='Creekbank (BC)';
        elevmsl=levelin * 0.0254 + 1.821;
        elevsur=levelin * 0.0254 + 0.693;
  end;
  else if (serialno eq 'S0213EF8') then do;
        location='Midmarsh (BM)';
        elevmsl=levelin * 0.0254 + 1.698;
        elevsur=levelin * 0.0254 + 1.035;
  end;
  else if (serialno eq 'S0213FFA') then do;
        location='High Marsh (BH)';
        elevmsl=levelin * 0.0254 + 1.624;
        elevsur=levelin * 0.0254 + 1.377;
  end;
  else location=serialno; 


label serialno= 'Logger Serial Number';
label elevmsl='Water Elevation above MSL - meters';
label elevsur='Water Elevation above ground surface - meters';
label levelin='Raw water level measurement - inches';
label dumpdate='Date of datalogger download';
label datelev='Date of level measurement'; 


proc sort;
  by serialno datelev dumpdate; 


data junk; /* a non-empty data set in case lib.wlr&year is empty */
  input serialno $ datelev date9. qualcode $ 1;
  cards;
  none 01Jan1900 N
  ; 


data olddata; /*combine datasets to create new non-empty dataset*/
  set lib.wlr&year junk;
*  set  junk; 


proc sort; by serialno datelev; 


data lib.wlr&year ;
  update olddata newhour; by serialno datelev;
  if (serialno eq 'none') then delete; 


data _null_; /*print out a new text file containing the data */
  set lib.wlr&year;
  file vcrwlr&year;
  date=datepart(datelev);
  time=timepart(datelev);
  format date MMDDYY10.;
  format time TIME8.;
  if (_n_ eq 1) then 
     put  'location, datelev, time, elevmsl, elevsur, levelin, qualcode, 
dumpdate' ;
  put '"' Location '", ' date ', ' time ', ' elevmsl ', ' elevsur 
      ', ' levelin ', "' qualcode '", ' dumpdate ; 


proc print;
run;

if (serialno eq 'S021408D') then do;
           location='Creekbank (BC)';
           elevmsl=levelin * 0.0254 + 1.821;
        end;
        else if (serialno eq 'S0213EF8') then do;
           location='Midmarsh (BM)';
              elevmsl=levelin * 0.0254 + 1.698;
        end;
        else if (serialno eq 'S0213FFA') then do;
           location='High Marsh (BH)';
              elevmsl=levelin * 0.0254 + 1.624;

if (serialno eq 'S021408D') then do;
        location='Creekbank (BC)';
        elevsur=levelin * 0.0254 + 0.693;
  end;
  else if (serialno eq 'S0213EF8') then do;
        location='Midmarsh (BM)';
        elevsur=levelin * 0.0254 + 1.035;
  end;
  else if (serialno eq 'S0213FFA') then do;
        location='High Marsh (BH)';
        elevsur=levelin * 0.0254 + 1.377;
  end;
